SELECT AVG(ss_quantity) AS avg_quantity, AVG(ss_list_price) AS avg_list_price, AVG(ss_sales_price) AS avg_sales_price, AVG(ss_coupon_amt) AS avg_coupon_amount FROM store_sales JOIN customer_demographics ON store_sales.ss_cdemo_sk = customer_demographics.cd_demo_sk JOIN customer_address ON store_sales.ss_addr_sk = customer_address.ca_address_sk JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk WHERE ca_state IN ('TN', 'TN', 'TN', 'TN', 'TN', 'TN') AND cd_education_status = 'College' AND cd_marital_status = 'S' AND cd_gender = 'M' AND d_year = 2002;
