SELECT CASE WHEN cs_quantity BETWEEN 1 AND 10 THEN 'Bucket 1' WHEN cs_quantity BETWEEN 11 AND 20 THEN 'Bucket 2' WHEN cs_quantity BETWEEN 21 AND 30 THEN 'Bucket 3' WHEN cs_quantity BETWEEN 31 AND 40 THEN 'Bucket 4' WHEN cs_quantity BETWEEN 41 AND 50 THEN 'Bucket 5' ELSE 'Bucket 6' END AS sales_bucket, AVG(cs_list_price) AS avg_list_price, COUNT(cs_list_price) AS non_null_list_prices, COUNT(DISTINCT cs_list_price) AS distinct_list_prices FROM catalog_sales WHERE cs_list_price IS NOT NULL GROUP BY CASE WHEN cs_quantity BETWEEN 1 AND 10 THEN 'Bucket 1' WHEN cs_quantity BETWEEN 11 AND 20 THEN 'Bucket 2' WHEN cs_quantity BETWEEN 21 AND 30 THEN 'Bucket 3' WHEN cs_quantity BETWEEN 31 AND 40 THEN 'Bucket 4' WHEN cs_quantity BETWEEN 41 AND 50 THEN 'Bucket 5' ELSE 'Bucket 6' END;
