SELECT CASE WHEN ss_item_sk BETWEEN 1 AND 100 THEN 'Bucket 1' WHEN ss_item_sk BETWEEN 101 AND 200 THEN 'Bucket 2' WHEN ss_item_sk BETWEEN 201 AND 300 THEN 'Bucket 3' WHEN ss_item_sk BETWEEN 301 AND 400 THEN 'Bucket 4' WHEN ss_item_sk BETWEEN 401 AND 500 THEN 'Bucket 5' WHEN ss_item_sk BETWEEN 501 AND 600 THEN 'Bucket 6' END AS sales_bucket, AVG(ss_list_price) AS avg_list_price, COUNT(ss_list_price) AS non_null_list_prices, COUNT(DISTINCT ss_list_price) AS distinct_list_prices FROM store_sales WHERE ss_coupon_amt IS NOT NULL AND ss_wholesale_cost IS NOT NULL GROUP BY sales_bucket;
