SELECT i.i_item_id, s.s_store_id, SUM(ss.ss_quantity) AS total_quantity_sold_store, SUM(sr.sr_return_quantity) AS total_quantity_returned, SUM(cs.cs_quantity) AS total_quantity_purchased_catalog FROM store_sales ss JOIN date_dim d1 ON ss.ss_sold_date_sk = d1.d_date_sk JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk JOIN date_dim d2 ON sr.sr_returned_date_sk = d2.d_date_sk JOIN catalog_sales cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk JOIN date_dim d3 ON cs.cs_sold_date_sk = d3.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d1.d_moy = 9 AND d1.d_year = 1999 AND d2.d_year = 1999 AND d2.d_moy BETWEEN 10 AND 12 AND d3.d_year BETWEEN 2000 AND 2002 GROUP BY i.i_item_id, s.s_store_id HAVING SUM(ss.ss_quantity) > 0 AND SUM(sr.sr_return_quantity) > 0 AND SUM(cs.cs_quantity) > 0;
