SELECT i.i_item_id, s.s_store_id, SUM(ss.ss_quantity) AS total_quantity_sold_store, SUM(sr.sr_return_quantity) AS total_quantity_returned, SUM(cs.cs_quantity) AS total_quantity_purchased_catalog FROM store_sales ss JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk JOIN catalog_sales cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk JOIN date_dim d_ss ON ss.ss_sold_date_sk = d_ss.d_date_sk JOIN date_dim d_sr ON sr.sr_returned_date_sk = d_sr.d_date_sk JOIN date_dim d_cs ON cs.cs_sold_date_sk = d_cs.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d_ss.d_month_seq = 1200 -- specific month and year AND d_sr.d_month_seq BETWEEN d_ss.d_month_seq + 1 AND d_ss.d_month_seq + 6 AND d_cs.d_year BETWEEN d_ss.d_year AND d_ss.d_year + 3 GROUP BY i.i_item_id, s.s_store_id;
