SELECT c.* FROM customer c JOIN web_returns wr ON c.c_customer_sk = wr.wr_refunded_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim d ON wr.wr_returned_date_sk = d.d_date_sk WHERE wr.wr_return_amt > 1.2 * ( SELECT AVG(wr2.wr_return_amt) FROM web_returns wr2 JOIN customer c2 ON wr2.wr_refunded_customer_sk = c2.c_customer_sk JOIN customer_address ca2 ON c2.c_current_addr_sk = ca2.ca_address_sk JOIN date_dim d2 ON wr2.wr_returned_date_sk = d2.d_date_sk WHERE ca2.ca_state = ca.ca_state AND d2.d_date BETWEEN d.d_date - INTERVAL '1 year' AND d.d_date ) ORDER BY c.c_customer_id, c.c_first_name, c.c_last_name;
