SELECT c.* FROM customer c JOIN web_returns wr ON c.c_customer_sk = wr.wr_refunded_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim d ON wr.wr_returned_date_sk = d.d_date_sk WHERE d.d_year = 2000 AND ca.ca_state = 'GA' AND wr.wr_return_amt > ( SELECT AVG(wr2.wr_return_amt) * 1.2 FROM web_returns wr2 JOIN customer_address ca2 ON wr2.wr_refunded_addr_sk = ca2.ca_address_sk JOIN date_dim d2 ON wr2.wr_returned_date_sk = d2.d_date_sk WHERE d2.d_year = 2000 AND ca2.ca_state = 'GA' ) ORDER BY c.c_customer_id;
