SELECT DISTINCT ca.ca_county FROM customer_address ca JOIN store_sales ss1 ON ca.ca_address_sk = ss1.ss_addr_sk JOIN store_sales ss2 ON ca.ca_address_sk = ss2.ss_addr_sk JOIN store_sales ss3 ON ca.ca_address_sk = ss3.ss_addr_sk JOIN web_sales ws1 ON ca.ca_address_sk = ws1.ws_bill_addr_sk JOIN web_sales ws2 ON ca.ca_address_sk = ws2.ws_bill_addr_sk JOIN web_sales ws3 ON ca.ca_address_sk = ws3.ws_bill_addr_sk JOIN date_dim d1 ON ss1.ss_sold_date_sk = d1.d_date_sk AND ws1.ws_sold_date_sk = d1.d_date_sk JOIN date_dim d2 ON ss2.ss_sold_date_sk = d2.d_date_sk AND ws2.ws_sold_date_sk = d2.d_date_sk JOIN date_dim d3 ON ss3.ss_sold_date_sk = d3.d_date_sk AND ws3.ws_sold_date_sk = d3.d_date_sk WHERE d1.d_year = 2001 AND d1.d_quarter_seq = d2.d_quarter_seq - 1 AND d2.d_quarter_seq = d3.d_quarter_seq - 1 AND ws1.ws_net_paid > ss1.ss_net_paid AND ws2.ws_net_paid > ss2.ss_net_paid AND ws3.ws_net_paid > ss3.ss_net_paid;
