SELECT SUM(cs_ext_discount_amt) AS total_discounted_amount FROM catalog_sales JOIN item ON cs_item_sk = i_item_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE i_manufact = 'Particular Manufacturer' AND d_date BETWEEN '2023-01-01' AND '2023-03-31' AND cs_ext_discount_amt > ( SELECT AVG(cs_ext_discount_amt) * 1.3 FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE d_date BETWEEN '2023-01-01' AND '2023-03-31' );
