SELECT SUM(cs_ext_discount_amt) AS total_discounted_amount FROM catalog_sales JOIN item ON cs_item_sk = i_item_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE i_manufact_id = 610 AND d_date BETWEEN DATE '2001-03-04' AND DATE '2001-06-02' AND cs_ext_discount_amt > ( SELECT AVG(cs_ext_discount_amt) * 1.3 FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE d_year = 2001 );
