SELECT i.i_manufact_id, SUM(cs.cs_ext_sales_price) AS total_sales, 'Catalog' AS sales_channel FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN customer_address ca ON cs.cs_bill_addr_sk = ca.ca_address_sk WHERE d.d_year = 2023 AND d.d_moy = 5 AND i.i_category = 'Electronics' AND ca.ca_gmt_offset = -5.00 GROUP BY i.i_manufact_id UNION ALL SELECT i.i_manufact_id, SUM(ss.ss_ext_sales_price) AS total_sales, 'Store' AS sales_channel FROM store_sales ss JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN customer_address ca ON ss.ss_addr_sk = ca.ca_address_sk WHERE d.d_year = 2023 AND d.d_moy = 5 AND i.i_category = 'Electronics' AND ca.ca_gmt_offset = -5.00 GROUP BY i.i_manufact_id UNION ALL SELECT i.i_manufact_id, SUM(ws.ws_ext_sales_price) AS total_sales, 'Web' AS sales_channel FROM web_sales ws JOIN item i ON ws.ws_item_sk = i.i_item_sk JOIN date_dim d ON ws.ws_sold_date_sk = d.d_date_sk JOIN customer_address ca ON ws.ws_bill_addr_sk = ca.ca_address_sk WHERE d.d_year = 2023 AND d.d_moy = 5 AND i.i_category = 'Electronics' AND ca.ca_gmt_offset = -5.00 GROUP BY i.i_manufact_id ORDER BY total_sales DESC;
