SELECT DISTINCT c.c_customer_id FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE hd.hd_buy_potential = '>10000' AND (CAST(hd.hd_dep_count AS DECIMAL) / NULLIF(hd.hd_vehicle_count, 0)) > 1.2 AND d.d_year IN (1999, 2000, 2001) AND ss.ss_quantity BETWEEN 15 AND 20 AND (d.d_dom <= 5 OR d.d_dom >= d.d_last_dom - 4) AND s.s_county = 'Williamson County' GROUP BY c.c_customer_id HAVING COUNT(DISTINCT d.d_year) = 3;
