SELECT ca.ca_state AS state, cd.cd_gender AS gender, cd.cd_marital_status AS marital_status, COUNT(DISTINCT c.c_customer_sk) AS count_of_customers, MIN(cd.cd_dep_count) AS min_dep_count, MAX(cd.cd_dep_count) AS max_dep_count, AVG(cd.cd_dep_count) AS avg_dep_count, COUNT(DISTINCT cd.cd_dep_count) AS count_distinct_dep_count, MIN(cd.cd_dep_employed_count) AS min_dep_employed_count, MAX(cd.cd_dep_employed_count) AS max_dep_employed_count, AVG(cd.cd_dep_employed_count) AS avg_dep_employed_count, COUNT(DISTINCT cd.cd_dep_employed_count) AS count_distinct_dep_employed_count, MIN(cd.cd_dep_college_count) AS min_dep_college_count, MAX(cd.cd_dep_college_count) AS max_dep_college_count, AVG(cd.cd_dep_college_count) AS avg_dep_college_count, COUNT(DISTINCT cd.cd_dep_college_count) AS count_distinct_dep_college_count, COUNT(DISTINCT c.c_customer_sk) AS count_of_customers_2, COUNT(DISTINCT c.c_customer_sk) AS count_of_customers_3 FROM customer c JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN date_dim dd ON ss.ss_sold_date_sk = dd.d_date_sk WHERE dd.d_year = 2022 GROUP BY ca.ca_state, cd.cd_gender, cd.cd_marital_status;
