SELECT i.i_item_id, i.i_item_desc, s.s_state, SUM(ss.ss_net_profit) / SUM(ss.ss_ext_sales_price) AS gross_profit_margin, RANK() OVER (PARTITION BY s.s_state ORDER BY SUM(ss.ss_net_profit) / SUM(ss.ss_ext_sales_price) DESC) AS profit_margin_rank FROM store_sales ss JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE d.d_year = ? AND s.s_state IN (?, ?, ?, ?) GROUP BY i.i_item_id, i.i_item_desc, s.s_state ORDER BY s.s_state, profit_margin_rank;
