SELECT DISTINCT i.i_item_id, i.i_current_price FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN promotion p ON cs.cs_promo_sk = p.p_promo_sk JOIN inventory inv ON i.i_item_sk = inv.inv_item_sk JOIN date_dim d ON inv.inv_date_sk = d.d_date_sk WHERE p.p_channel_catalog = 'Y' AND i.i_current_price BETWEEN 30 AND 60 AND i.i_manufact_id IN (/* list of manufacturer ids */ 1, 2, 3) AND inv.inv_quantity_on_hand BETWEEN 100 AND 500 AND d.d_date BETWEEN CURRENT_DATE - INTERVAL '60' DAY AND CURRENT_DATE;
