SELECT DISTINCT i.i_item_id, i.i_current_price FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN inventory inv ON i.i_item_sk = inv.inv_item_sk JOIN promotion p ON cs.cs_promo_sk = p.p_promo_sk WHERE i.i_current_price BETWEEN 38 AND 68 AND i.i_manufact_id IN (677, 940, 694, 808) AND p.p_channel_catalog = 'Y' AND d.d_date BETWEEN DATE '2000-02-01' AND DATE '2000-04-01' AND inv.inv_date_sk BETWEEN d.d_date_sk AND d.d_date_sk + 60 AND inv.inv_quantity_on_hand BETWEEN 100 AND 500;
