SELECT i.i_item_id, i.i_current_price FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN inventory inv ON i.i_item_sk = inv.inv_item_sk JOIN date_dim invd ON inv.inv_date_sk = invd.d_date_sk WHERE i.i_manufact_id IN (677, 940, 694, 808) AND i.i_current_price BETWEEN 68 AND 98 AND inv.inv_quantity_on_hand BETWEEN 100 AND 500 AND invd.d_date BETWEEN '2000-02-01' AND '2000-04-01' AND cs.cs_quantity BETWEEN 100 AND 500 GROUP BY i.i_item_id, i.i_current_price;
