SELECT COUNT(DISTINCT c_customer_sk) AS customer_count FROM customer WHERE c_customer_sk IN ( SELECT cs_bill_customer_sk FROM catalog_sales JOIN date_dim ON catalog_sales.cs_sold_date_sk = date_dim.d_date_sk WHERE d_year = 2023 ) AND c_customer_sk IN ( SELECT ss_customer_sk FROM store_sales JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk WHERE d_year = 2023 ) AND c_customer_sk IN ( SELECT ws_bill_customer_sk FROM web_sales JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE d_year = 2023 );
