SELECT i.i_item_id, i.i_product_name, MAX(profit) AS best_net_profit, MIN(profit) AS worst_net_profit FROM ( SELECT ss_item_sk AS item_sk, SUM(ss_net_profit) AS profit FROM store_sales WHERE ss_addr_sk = 1 AND ss_store_sk = 4 GROUP BY ss_item_sk UNION ALL SELECT cs_item_sk AS item_sk, SUM(cs_net_profit) AS profit FROM catalog_sales GROUP BY cs_item_sk UNION ALL SELECT ws_item_sk AS item_sk, SUM(ws_net_profit) AS profit FROM web_sales GROUP BY ws_item_sk ) sales JOIN item i ON sales.item_sk = i.i_item_sk GROUP BY i.i_item_id, i.i_product_name ORDER BY best_net_profit DESC, worst_net_profit ASC LIMIT 1;
