SELECT SUM(ws_ext_sales_price) AS total_web_sales FROM web_sales JOIN customer_address ON web_sales.ws_bill_addr_sk = customer_address.ca_address_sk JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE (ca_zip IN ('specific_zip_codes') OR ca_city IN ('specific_cities') OR ca_county IN ('specific_counties') OR ca_state IN ('specific_states')) AND (ws_item_sk IN (SELECT i_item_sk FROM item WHERE i_item_id IN ('specific_item_ids'))) AND d_year = <given_year> AND d_qoy = <given_quarter>;
