SELECT SUM(ws_ext_sales_price) AS total_web_sales FROM web_sales JOIN customer_address ON web_sales.ws_bill_addr_sk = customer_address.ca_address_sk JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE (ca_zip = '12345' OR ca_city = 'San Francisco' OR ca_county = 'Los Angeles' OR ca_state = 'CA') AND d_year = 2001 AND d_qoy = 2;
