SELECT SUM(ws_ext_sales_price) AS total_web_sales FROM web_sales JOIN customer_address ON web_sales.ws_bill_addr_sk = customer_address.ca_address_sk JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE (ca_zip = 'specific_zip' OR ca_city = 'specific_city' OR ca_county = 'specific_county' OR ca_state = 'specific_state') AND d_year = 2000 AND d_qoy = 1;
