SELECT SUM(ws_net_paid) AS total_web_sales FROM web_sales JOIN customer_address ON web_sales.ws_bill_addr_sk = customer_address.ca_address_sk JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE (ca_zip IN ('12345', '67890') OR ca_city IN ('CityA', 'CityB') OR ca_county IN ('CountyA', 'CountyB') OR ca_state IN ('StateA', 'StateB')) AND (ws_item_sk IN (101, 102, 103)) AND d_year = 2022 AND d_qoy = 1;
