SELECT SUM(ws_net_paid) AS total_web_sales FROM web_sales JOIN customer ON web_sales.ws_bill_customer_sk = customer.c_customer_sk JOIN customer_address ON customer.c_current_addr_sk = customer_address.ca_address_sk JOIN date_dim ON web_sales.ws_sold_date_sk = date_dim.d_date_sk WHERE (ca_zip = '12345' OR ca_city = 'SomeCity' OR ca_county = 'SomeCounty' OR ca_state = 'SomeState') AND d_year = 2001 AND d_qoy = 2;
