SELECT ca.ca_city AS customer_city, s.s_city AS store_city, ss.ss_coupon_amt AS coupon_amount, ss.ss_net_profit AS net_profit FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE ca.ca_city <> s.s_city AND s.s_city IN ('City1', 'City2', 'City3', 'City4', 'City5') AND d.d_weekend = 'Y' AND d.d_year BETWEEN 2021 AND 2023 AND cd.cd_dep_count = specific_dependent_count AND hd.hd_vehicle_count = specific_vehicle_count;
