SELECT cd.cd_marital_status, cd.cd_education_status, ca.ca_state, SUM(cs.cs_sales_price) AS total_sales, SUM(cs.cs_net_profit) AS total_profit FROM catalog_sales cs JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk GROUP BY cd.cd_marital_status, cd.cd_education_status, ca.ca_state;
