SELECT COUNT(DISTINCT CASE WHEN ss_promo_sk IS NOT NULL THEN ss_ticket_number END) AS promo_sales_count, COUNT(DISTINCT ss_ticket_number) AS total_sales_count, COUNT(DISTINCT CASE WHEN ss_promo_sk IS NOT NULL THEN ss_ticket_number END) * 1.0 / COUNT(DISTINCT ss_ticket_number) AS promo_sales_ratio FROM store_sales JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk JOIN store ON store_sales.ss_store_sk = store.s_store_sk JOIN item ON store_sales.ss_item_sk = item.i_item_sk WHERE date_dim.d_year = 2023 AND date_dim.d_moy = 5 AND store.s_gmt_offset = -5.00 AND item.i_category IN ('Electronics', 'Clothing');
