SELECT i.i_manufact_id, SUM(CASE WHEN d.d_quarter_seq = 1 THEN cs.cs_ext_sales_price ELSE 0 END) AS quarterly_sales_q1, SUM(CASE WHEN d.d_quarter_seq = 2 THEN cs.cs_ext_sales_price ELSE 0 END) AS quarterly_sales_q2, SUM(CASE WHEN d.d_quarter_seq = 3 THEN cs.cs_ext_sales_price ELSE 0 END) AS quarterly_sales_q3, SUM(CASE WHEN d.d_quarter_seq = 4 THEN cs.cs_ext_sales_price ELSE 0 END) AS quarterly_sales_q4, SUM(cs.cs_ext_sales_price) AS yearly_sales FROM catalog_sales cs JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk WHERE d.d_year = 2000 AND d.d_moy = 11 AND i.i_brand = 'SPECIFIC_BRAND' GROUP BY i.i_manufact_id HAVING AVG(cs.cs_ext_sales_price) > 0.1 * (SUM(cs.cs_ext_sales_price) / COUNT(DISTINCT d.d_month_seq));
