SELECT i.i_manufact_id AS ManufacturerID, SUM(cs.cs_ext_sales_price) / 4 AS QuarterlySales, SUM(cs.cs_ext_sales_price) AS YearlySales FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk WHERE i.i_item_desc LIKE '%specific characteristics%' AND dd.d_year = 2023 GROUP BY i.i_manufact_id HAVING AVG(cs.cs_ext_sales_price / 12) > 0.1 * 1200;
