WITH web_catalog_purchases AS ( SELECT DISTINCT cs_bill_customer_sk AS customer_sk FROM catalog_sales JOIN item ON cs_item_sk = i_item_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE i_class = 'fitness' AND i_category = 'Sports' AND d_moy = 5 AND d_year = 2000 UNION SELECT DISTINCT ws_bill_customer_sk AS customer_sk FROM web_sales JOIN item ON ws_item_sk = i_item_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE i_class = 'fitness' AND i_category = 'Sports' AND d_moy = 5 AND d_year = 2000 ), in_store_purchases AS ( SELECT DISTINCT ss_customer_sk AS customer_sk FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_month_seq BETWEEN (SELECT d_month_seq FROM date_dim WHERE d_moy = 5 AND d_year = 2000) + 1 AND (SELECT d_month_seq FROM date_dim WHERE d_moy = 5 AND d_year = 2000) + 3 ), eligible_customers AS ( SELECT customer_sk FROM web_catalog_purchases INTERSECT SELECT customer_sk FROM in_store_purchases ), customer_revenue AS ( SELECT cs_bill_customer_sk AS customer_sk, SUM(cs_net_paid_inc_tax) AS total_revenue FROM catalog_sales WHERE cs_bill_customer_sk IN (SELECT customer_sk FROM eligible_customers) GROUP BY cs_bill_customer_sk UNION ALL SELECT ws_bill_customer_sk AS customer_sk, SUM(ws_net_paid_inc_tax) AS total_revenue FROM web_sales WHERE ws_bill_customer_sk IN (SELECT customer_sk FROM eligible_customers) GROUP BY ws_bill_customer_sk ) SELECT FLOOR(total_revenue / 50) * 50 AS revenue_segment, COUNT(DISTINCT customer_sk) AS customer_count FROM customer_revenue GROUP BY revenue_segment ORDER BY revenue_segment;
