SELECT i.i_item_id, SUM(cs.cs_net_paid_inc_tax + ss.ss_net_paid_inc_tax + ws.ws_net_paid_inc_tax) AS total_sales_amount FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN store_sales ss ON i.i_item_sk = ss.ss_item_sk JOIN web_sales ws ON i.i_item_sk = ws.ws_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk AND ss.ss_sold_date_sk = d.d_date_sk AND ws.ws_sold_date_sk = d.d_date_sk JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk AND ss.ss_customer_sk = c.c_customer_sk AND ws.ws_bill_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE d.d_year = 2023 AND d.d_moy = 5 AND i.i_color IN ('Red', 'Blue', 'Green') AND ca.ca_gmt_offset = -5.00 GROUP BY i.i_item_id ORDER BY total_sales_amount DESC;
