SELECT i.i_item_id, SUM(cs.cs_ext_sales_price) + SUM(ss.ss_ext_sales_price) + SUM(ws.ws_ext_sales_price) AS total_sales_amount FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN store_sales ss ON i.i_item_sk = ss.ss_item_sk JOIN web_sales ws ON i.i_item_sk = ws.ws_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk AND ss.ss_sold_date_sk = d.d_date_sk AND ws.ws_sold_date_sk = d.d_date_sk JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk AND ss.ss_customer_sk = c.c_customer_sk AND ws.ws_bill_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE i.i_color IN ('slate', 'blanched', 'burnished') AND d.d_year = 2001 AND d.d_moy = 2 AND ca.ca_gmt_offset = -5 GROUP BY i.i_item_id ORDER BY total_sales_amount DESC;
