SELECT i.i_item_id, SUM(cs.cs_ext_sales_price) + SUM(ss.ss_ext_sales_price) + SUM(ws.ws_ext_sales_price) AS total_sales_amount FROM item i JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN store_sales ss ON i.i_item_sk = ss.ss_item_sk JOIN web_sales ws ON i.i_item_sk = ws.ws_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk AND ss.ss_sold_date_sk = d.d_date_sk AND ws.ws_sold_date_sk = d.d_date_sk JOIN customer_address ca ON cs.cs_bill_addr_sk = ca.ca_address_sk AND ss.ss_addr_sk = ca.ca_address_sk AND ws.ws_bill_addr_sk = ca.ca_address_sk WHERE i.i_color IN ('firebrick', 'rosy', 'white') AND d.d_moy = 3 AND d.d_year = 1998 AND ca.ca_gmt_offset = -6 GROUP BY i.i_item_id ORDER BY total_sales_amount DESC;
