SELECT i.i_item_id, SUM(ss.ss_ext_sales_price) AS store_revenue, SUM(cs.cs_ext_sales_price) AS catalog_revenue, SUM(ws.ws_ext_sales_price) AS web_revenue FROM item i JOIN store_sales ss ON i.i_item_sk = ss.ss_item_sk JOIN catalog_sales cs ON i.i_item_sk = cs.cs_item_sk JOIN web_sales ws ON i.i_item_sk = ws.ws_item_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk AND cs.cs_sold_date_sk = d.d_date_sk AND ws.ws_sold_date_sk = d.d_date_sk WHERE d.d_date BETWEEN '2002-02-19' AND '2002-02-25' GROUP BY i.i_item_id HAVING ABS(SUM(ss.ss_ext_sales_price) - SUM(cs.cs_ext_sales_price)) < 0.1 * SUM(ss.ss_ext_sales_price) AND ABS(SUM(ss.ss_ext_sales_price) - SUM(ws.ws_ext_sales_price)) < 0.1 * SUM(ss.ss_ext_sales_price) AND ABS(SUM(cs.cs_ext_sales_price) - SUM(ws.ws_ext_sales_price)) < 0.1 * SUM(cs.cs_ext_sales_price) ORDER BY (SUM(ss.ss_ext_sales_price) + SUM(cs.cs_ext_sales_price) + SUM(ws.ws_ext_sales_price)) DESC LIMIT 1;
