SELECT SUM(CASE WHEN cs_promo_sk IS NOT NULL THEN cs_quantity ELSE 0 END) / NULLIF(SUM(CASE WHEN cs_promo_sk IS NULL THEN cs_quantity ELSE 0 END), 0) AS promo_to_nonpromo_ratio FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk JOIN customer_address ON cs_bill_addr_sk = ca_address_sk JOIN item ON cs_item_sk = i_item_sk WHERE d_month_seq = 202109 AND i_category IN ('Category1', 'Category2') AND ca_gmt_offset = -5.00;
