SELECT SUM(CASE WHEN cs_promo_sk IS NOT NULL THEN cs_quantity ELSE 0 END) / NULLIF(SUM(CASE WHEN cs_promo_sk IS NULL THEN cs_quantity ELSE 0 END), 0) AS promo_to_non_promo_ratio FROM catalog_sales JOIN date_dim ON cs_sold_date_sk = d_date_sk JOIN customer_address ON cs_bill_addr_sk = ca_address_sk JOIN item ON cs_item_sk = i_item_sk WHERE d_moy = 12 AND d_year = 2001 AND ca_gmt_offset = -7 AND i_category = 'Books';
