WITH MonthlySales AS ( SELECT s.s_manager, d.d_month_seq, SUM(ss.ss_net_paid) AS monthly_sales FROM store_sales ss JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d.d_year = ? AND i.i_category IN (?, ?, ?) AND i.i_class IN (?, ?, ?) AND i.i_brand IN (?, ?, ?) GROUP BY s.s_manager, d.d_month_seq ), YearlyAverageSales AS ( SELECT s_manager, AVG(monthly_sales) AS yearly_avg_sales FROM MonthlySales GROUP BY s_manager ) SELECT ms.s_manager, ms.d_month_seq, ms.monthly_sales, yas.yearly_avg_sales FROM MonthlySales ms JOIN YearlyAverageSales yas ON ms.s_manager = yas.s_manager ORDER BY ms.s_manager, ms.d_month_seq;
