SELECT s.s_manager, d.d_moy AS month, SUM(ss.ss_net_paid) AS monthly_sales, AVG(SUM(ss.ss_net_paid)) OVER (PARTITION BY s.s_manager) AS yearly_avg_sales FROM store_sales ss JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE d.d_year = 2023 AND i.i_category_id = 1200 GROUP BY s.s_manager, d.d_moy ORDER BY s.s_manager, d.d_moy;
