SELECT s.s_store_id FROM store s JOIN store_sales ss ON s.s_store_sk = ss.ss_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE d.d_year = 1999 AND i.i_color IN ('purple', 'burlywood', 'indian', 'spring', 'floral', 'medium') AND i.i_current_price > 64 GROUP BY s.s_store_id HAVING SUM(ss.ss_quantity) > ( SELECT SUM(cs.cs_quantity) FROM catalog_sales cs JOIN item i2 ON cs.cs_item_sk = i2.i_item_sk JOIN date_dim d2 ON cs.cs_sold_date_sk = d2.d_date_sk WHERE d2.d_year = 1999 AND i2.i_color IN ('purple', 'burlywood', 'indian', 'spring', 'floral', 'medium') AND i2.i_current_price > 64 AND cs.cs_call_center_sk IS NOT NULL ) AND SUM(ss.ss_quantity) > ( SELECT SUM(ws.ws_quantity) FROM web_sales ws JOIN item i3 ON ws.ws_item_sk = i3.i_item_sk JOIN date_dim d3 ON ws.ws_sold_date_sk = d3.d_date_sk WHERE d3.d_year = 1999 AND i3.i_color IN ('purple', 'burlywood', 'indian', 'spring', 'floral', 'medium') AND i3.i_current_price > 64 AND ws.ws_web_page_sk IS NOT NULL );
