SELECT s.s_store_id, i.i_item_id, SUM(ss.ss_ext_sales_price) AS item_revenue FROM store_sales ss JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE s.s_store_id = 'DMS.01' GROUP BY s.s_store_id, i.i_item_id HAVING SUM(ss.ss_ext_sales_price) < 0.1 * ( SELECT AVG(total_revenue) FROM ( SELECT SUM(ss_inner.ss_ext_sales_price) AS total_revenue FROM store_sales ss_inner JOIN store s_inner ON ss_inner.ss_store_sk = s_inner.s_store_sk WHERE s_inner.s_store_id = 'DMS.01' GROUP BY ss_inner.ss_item_sk ) AS avg_revenue_per_item ) ORDER BY s.s_store_id, item_revenue;
