SELECT s.s_store_id, i.i_item_id, SUM(ss.ss_ext_sales_price) AS item_revenue FROM store_sales ss JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE ss.ss_sold_date_sk BETWEEN 1176 AND 1176 GROUP BY s.s_store_id, i.i_item_id HAVING SUM(ss.ss_ext_sales_price) < 0.1 * ( SELECT AVG(store_total_revenue) FROM ( SELECT SUM(ss_inner.ss_ext_sales_price) AS store_total_revenue FROM store_sales ss_inner WHERE ss_inner.ss_store_sk = s.s_store_sk AND ss_inner.ss_sold_date_sk BETWEEN 1176 AND 1176 GROUP BY ss_inner.ss_store_sk ) AS store_revenue ) ORDER BY s.s_store_id, i.i_item_id;
