SELECT c.c_customer_id, SUM(ss.ss_ext_sales_price) AS total_ext_sales_price, SUM(ss.ss_ext_list_price) AS total_ext_list_price, SUM(ss.ss_ext_tax) AS total_ext_tax FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN household_demographics hd ON ss.ss_hdemo_sk = hd.hd_demo_sk WHERE ca.ca_city NOT IN (s.s_city) AND s.s_city IN ('City1', 'City2') AND d.d_dom IN (1, 2) AND d.d_year BETWEEN 2021 AND 2023 AND hd.hd_dep_count = <specific_dep_count> AND hd.hd_vehicle_count = <specific_vehicle_count> GROUP BY c.c_customer_id;
