SELECT c.c_customer_id, SUM(ss.ss_ext_sales_price) AS total_ext_sales_price, SUM(ss.ss_ext_list_price) AS total_ext_list_price, SUM(ss.ss_ext_tax) AS total_ext_tax FROM store_sales ss JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE s.s_city IN ('Midway', 'Fairview') AND d.d_year BETWEEN 1998 AND 2000 AND d.d_dom IN (1, 2) AND cd.cd_dep_count = 8 AND hd.hd_vehicle_count = 3 GROUP BY c.c_customer_id;
