SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk LEFT JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk LEFT JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk WHERE ca.ca_state IN ('state1', 'state2', 'state3') -- replace with actual states AND d.d_year = 2023 -- replace with actual year AND d.d_month_seq BETWEEN (SELECT d_month_seq FROM date_dim WHERE d_year = 2023 AND d_moy = 1) -- replace with actual start month AND (SELECT d_month_seq FROM date_dim WHERE d_year = 2023 AND d_moy = 2) -- replace with actual end month AND cs.cs_order_number IS NULL AND ws.ws_order_number IS NULL GROUP BY cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating;
