SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE ca.ca_state IN ('CA', 'NY', 'TX') -- Example states AND d.d_year = 2022 -- Example year AND d.d_month_seq BETWEEN 245 AND 246 -- Example two-month period AND c.c_customer_sk NOT IN ( SELECT cs.cs_bill_customer_sk FROM catalog_sales cs WHERE cs.cs_bill_customer_sk IS NOT NULL ) AND c.c_customer_sk NOT IN ( SELECT ws.ws_bill_customer_sk FROM web_sales ws WHERE ws.ws_bill_customer_sk IS NOT NULL ) GROUP BY cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating;
