SELECT ca.ca_state FROM catalog_sales cs JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk WHERE dd.d_month_seq = 1 + (12 * (2001 - 1900)) -- January 2001 AND cs.cs_sales_price >= 1.2 * ( SELECT AVG(i2.i_current_price) FROM item i2 WHERE i2.i_category = i.i_category ) GROUP BY ca.ca_state HAVING COUNT(DISTINCT cs.cs_bill_customer_sk) >= 10;
