SELECT ca.ca_state FROM customer_address ca JOIN customer c ON ca.ca_address_sk = c.c_current_addr_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk WHERE dd.d_month_seq = 7 + (1998 - 1900) * 12 AND cs.cs_sales_price >= 1.2 * ( SELECT AVG(i2.i_current_price) FROM item i2 WHERE i2.i_category = i.i_category ) GROUP BY ca.ca_state HAVING COUNT(DISTINCT c.c_customer_sk) >= 10;
