SELECT AVG(ss_quantity) AS avg_quantity, AVG(ss_list_price) AS avg_list_price, AVG(ss_ext_discount_amt) AS avg_discount, AVG(ss_sales_price) AS avg_sales_price FROM store_sales JOIN promotion ON ss_promo_sk = p_promo_sk JOIN customer_demographics ON ss_cdemo_sk = cd_demo_sk WHERE p_channel_dmail = 'N' AND p_channel_event = 'N' AND cd_gender = 'specific_gender' AND cd_marital_status = 'specific_marital_status' AND cd_education_status = 'specific_education_status';
