SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE hd.hd_buy_potential IN ('0-500', '>10000') AND hd.hd_dep_count > hd.hd_vehicle_count AND s.s_county = 'Williamson County' AND d.d_year BETWEEN 2000 AND 2002 AND d.d_dom IN (1, 2) AND ss.ss_quantity BETWEEN 1 AND 5 GROUP BY c.c_customer_sk HAVING COUNT(DISTINCT d.d_year) = 3;
