SELECT i_brand, i_class, i_category, SUM(cs.cs_sales_price) AS total_sales FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_category = 'Men' AND d.d_year IN (2001, 2002) GROUP BY i_brand, i_class, i_category ORDER BY i_brand, i_class, i_category;
